mapper.update:
update mfg_member
set user_phone=#{user_phone}
where user_id=#{user_id}

mapper.select:
select *
from mfg_member
where user_id=#{user_id} and user_name=#{user_name}

select user_id
from mfg_member
where user_name=#{user_name} and user_phone=#{user_phone}

select user_pw
from mfg_member
where user_id=#{user_id} and user_phone=#{user_phone}

mapper.update:
update mfg_member
set user_pw=#{user_pw}
where user_id=#{user_id}

update mfg_member 
		
	user_name=#{user_name},
	user_pw=#{user_pw} ,
	
		latitude =#{latitude},
	
	
		longitude =#{longitude}
					

where user_id=#{user_id}

mapper.select:
select count(*)
from mfg_member
where user_id=#{user_id} and user_pw=#{user_pw}

mapper.update:
update mfg_member
set roadFullAddr=#{roadFullAddr}
where user_id=#{user_id}

mapper.select:
select count(*)
from mfg_like
where buyer_id=#{buyer_id}
