mapper.select:
select user_id
from mfg_member
where user_name=#{user_name} and user_phone=#{user_phone}

select user_pw
from mfg_member
where user_id=#{user_id} and user_phone=#{user_phone}

mapper.update:
update mfg_member
set user_pw=#{user_pw}
where user_id=#{user_id}

mapper.select:
select count(*)
from mfg_member
where user_id=#{user_id} and user_pw=#{user_pw}
