mapper.select:
select * from mfg_products
where category = #{category}
order by prod_date desc
limit #{pageStart},#{perPageNum}

select * from mfg_products
where category = #{category}
and gender = #{gender}
order by prod_date desc
limit #{pageStart},#{perPageNum}

select * from mfg_products
where
category = #{category}
order by like_count desc
limit #{pageStart},#{perPageNum}

select * from mfg_products
where category = #{category}
and gender = #{gender}
order by like_count desc
limit #{pageStart},#{perPageNum}

select count(*)
from mfg_products
where category = #{category}

select count(*)
from mfg_products
where category = #{category}
and gender = #{gender}

mapper.update:
update mfg_products 
set viewcnt=viewcnt+1 
where prod_num = #{prod_num}

mapper.select:
select * from mfg_products 
order by prod_num desc
limit #{pageStart},#{perPageNum}
